import { Utils } from '../../support/utils';
import '../../support/api-beforeAfterBlock';
import "./General_POST_api_v1_iam_token";



let config;
let usecasename = 'reporting'
let specfilename = 'matrix-cxpm-reporting'
let configPath, execLogObj;
let apiDiffResponse = {}

describe('Testing matrix for cxpm-reporting', () => {
  before(() => {
    configPath = `cypress/e2e/${Cypress.env('integrationFolder')}/${usecasename}-config.json`;
    cy.before(config, configPath, execLogObj, usecasename, apiDiffResponse).then(value => {
      ({config, execLogObj, configPath, apiDiffResponse} = value)
    });
  });
  after(() => {
    cy.after(config, execLogObj, configPath, apiDiffResponse).then(value => ({config, execLogObj} = value));
  });
  it('Testing General_POST_api_v1_iam_token', () => {
    cy.on('fail',(err,runnable)=>{
        let error= JSON.stringify(err)
        let index = execLogObj.testCases.length-1
        if(index>=0){
            execLogObj.testCases[execLogObj.testCases.length-1].childTestCases.push({
              name:'***************ERROR !! *****************         '+ error,
              passFailFlag: 'Fail',
              duration:Utils.calcTimeDuration(config.currentTs, (new Date()).getTime())
          })
          execLogObj.testCases[index].duration = Utils.calcTimeDuration(config.testCaseStartTs, (new Date()).getTime())
          execLogObj.testCases[index].passFailFlag =  'Fail'
        }          
        throw err;
    }).then(()=>{
        cy.task('setLogObject',{val:execLogObj,num:3})
      })           
    var argsObj = {
        	payload:config.payload,
    };
    cy.General_POST_api_v1_iam_token(config, '1000', argsObj, execLogObj).then((response) => {              

    }); 
});

});

import { Utils } from '../../support/utils';
import '@shelex/cypress-allure-plugin';

Cypress.Commands.add("General_POST_api_v1_iam_token", (config, ticketNbr, argsObj, logObj) => {
