Cypress.Commands.add( "General_GET_api_v1_forecast_get_auto_step_size", (config, ticketNbr, argsObj, logObj ) => {

  let startTs = (new Date()).getTime()
  config.testCaseStartTs = config.currentTs= startTs;
  logObj.testCases.push({
    name:  "get-auto-step-size",
    passFailFlag: 'Pass',
    duration: (new Date()).getTime(),
    tag: "Forecasting",
    childTestCases: [
      {
        name: 'Is resp.status = 200',
        passFailFlag: 'Fail',
        duration: (new Date()).getTime()
      },
      {
        name: 'Is resp.isOkStatusCode = true',
        passFailFlag: 'Fail'
      }
    ]
  })

  let apiResponse,index= logObj.testCases.length-1;
  cy.request({
    method:  "GET",
    url: config.baseUrl + "/api/v1/forecast/get-auto-step-size?start_date=2023-09-24%2000:00:00&end_date=2023-09-29%2017:15:00&node_id=43&kpi_id=17&index_id=16&index_id=18&index_id=14",
    headers: {
      //'Authorization': 'basic ' + btoa(argsObj.username + ':' + argsObj.password),
      'Cookie': 'access=' + argsObj.accessToken+';refresh='+argsObj.refreshToken+';TENANT='+argsObj.tenant,
      'Content-Type': 'application/json',
      'Accept': 'application/json',
    },
    failOnStatusCode : false,
    body: argsObj.payload ? argsObj.payload : {}
  }).then(resp => {
    console.log('\n\n resp ==>'); console.log(resp);

    apiResponse = resp;
    let responseStatusCode =  "200";
    let responseType = "object";

    logObj.testCases[index].childTestCases[0] = {
      name: `Response code should be 2xx. The Response code is ${resp.status}`,
      json: resp.body,
      passFailFlag: resp.status >= 200 && resp.status <= 299 ? 'Pass' : 'Fail',
      duration: Utils.calcTimeDuration(logObj.testCases[index].childTestCases[0].duration, (new Date()).getTime())
    };
    config.currentTs= startTs = (new Date()).getTime()

    logObj.testCases[index].childTestCases[1] = {
      name: `"isOkStatusCode" of Response is true`,
      passFailFlag: resp.isOkStatusCode ? 'Pass' : 'Fail',
      duration: Utils.calcTimeDuration(startTs, (new Date()).getTime())
    };
    config.currentTs= startTs = (new Date()).getTime()

    if(resp.status >= 200 && resp.status <= 299) {        
       logObj.testCases[index].childTestCases.push({
                name: 'Is response body of type' + responseType,
                passFailFlag: typeof(resp.body) === "object" ? 'Pass' : 'Fail',
                duration: Utils.calcTimeDuration(startTs, (new Date()).getTime())
                });
                config.currentTs= startTs = (new Date()).getTime()
       logObj.testCases[index].childTestCases.push({
                name: 'Type of response body should be "object" and respBody should not null',
                passFailFlag: "object" === "object" && resp.body !== null ? 'Pass' : 'Fail',
                duration: Utils.calcTimeDuration(startTs, (new Date()).getTime())
                });
                config.currentTs= startTs = (new Date()).getTime()  

      logObj.testCases[index].duration = Utils.calcTimeDuration(config.testCaseStartTs, (new Date()).getTime())
      logObj.testCases[index].passFailFlag = logObj.testCases[index].childTestCases.filter(p => p.passFailFlag === 'Fail').length > 0 ? 'Fail' : 'Pass'    
      return apiResponse;
    }
  })
});

